mapper.select:
select 

from tb_wx_qrcode
where scene_id = #{sceneId,jdbcType=INTEGER} and is_perm = #{isPerm,jdbcType=CHAR}

    select count(1)
    from tb_wx_qrcode


       and is_perm = #{params.isPerm,jdbcType=CHAR}


       and scene_id = #{params.sceneId,jdbcType=INTEGER}


       and ticket like concat(concat('%',#{params.ticket,jdbcType=VARCHAR}),'%')


       and (expire_seconds > 0 and ((now()-create_time) >= expire_seconds))


       and ((expire_seconds > 0 and ((now()-create_time) < expire_seconds)) or expire_seconds = -1)


	   and create_time >= #{params.beginTime}


	   and create_time <= #{params.endTime}

    select 

    from tb_wx_qrcode


       and is_perm = #{params.isPerm,jdbcType=CHAR}


       and scene_id = #{params.sceneId,jdbcType=INTEGER}


       and ticket like concat(concat('%',#{params.ticket,jdbcType=VARCHAR}),'%')


       and (expire_seconds > 0 and ((now()-create_time) >= expire_seconds))


       and ((expire_seconds > 0 and ((now()-create_time) < expire_seconds)) or expire_seconds = -1)


	   and create_time >= #{params.beginTime}


	   and create_time <= #{params.endTime} 


    order by create_time desc 
    limit #{pageCond.begin},#{pageCond.pageSize}
